mapper.update:
update nurse
set name = #{name,jdbcType=VARCHAR},
  gender = #{gender,jdbcType=VARCHAR},
  age = #{age,jdbcType=INTEGER},
  number = #{number,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
SELECT * from nurse WHERE id NOT IN
  (
    SELECT nurse_id FROM nurse_schedule,operation WHERE id=nurse_schedule.operation_id AND date =#{date} AND start_time < #{endTime} AND end_time > #{startTime}
   )
